import React, { useState, useEffect } from 'react';
import Lottie from 'lottie-react';

function IntroAnimation({ onAnimationComplete }) {
  const [animationState, setAnimationState] = useState('playing');
  const [showContent, setShowContent] = useState(false);
  const [animationData, setAnimationData] = useState(null);

  useEffect(() => {
    // Lottie 애니메이션 데이터 로드
    fetch('https://lottie.host/a2453534-3e5d-4773-be3b-a41dd047268c/jARK1MRIV4.lottie')
      .then(response => response.json())
      .then(data => {
        setAnimationData(data);
      })
      .catch(error => {
        console.error('Lottie 애니메이션 로드 실패:', error);
        // 애니메이션 로드 실패 시에도 계속 진행
        setTimeout(() => {
          setAnimationState('completed');
          setTimeout(() => {
            onAnimationComplete();
          }, 1500);
        }, 3000);
      });

    // 애니메이션 시작 후 텍스트 표시
    const textTimer = setTimeout(() => {
      setShowContent(true);
    }, 500);

    return () => clearTimeout(textTimer);
  }, [onAnimationComplete]);

  const handleAnimationComplete = () => {
    setAnimationState('completed');

    // 애니메이션 완료 후 페이드 아웃
    setTimeout(() => {
      onAnimationComplete();
    }, 1500);
  };

  return (
    <div className="intro-animation">
      <div className="intro-content">
        <div className="lottie-container">
          {animationData ? (
            <Lottie
              animationData={animationData}
              loop={false}
              autoplay={true}
              style={{ 
                width: 300, 
                height: 300,
                filter: 'drop-shadow(0 10px 20px rgba(0,0,0,0.3))'
              }}
              onComplete={handleAnimationComplete}
            />
          ) : (
            <div className="loading-placeholder">
              <div className="spinner"></div>
            </div>
          )}
        </div>

        {showContent && (
          <div className="intro-text">
            <h1 className="brand-title">CHACHACHA</h1>
            <p className="welcome-text">Welcome to Matchroad</p>
            <div className="loading-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        )}
      </div>

      {animationState === 'completed' && (
        <div className="fade-out-overlay"></div>
      )}
    </div>
  );
}

export default IntroAnimation;
